import toast from "react-hot-toast";

export const usernameValidate = (values, props /* only available when using withFormik */) => {
    const errors = {};
    if (!values.username) {
        errors.username = toast.error('Username Required...!');
    } else if (values.username.includes(" ")) {
        errors.username = toast.error('Invalid Username..!');
    }

    return errors;
};

export const password = (values, props) => {
    let regularExpression  = /^[a-zA-Z0-9!@#$%^&*]{6,16}$/;
    const errors = {};
    if (!values.password) {
        errors.password = toast.error('Password Required...!');
    } else if (values.username.includes(" ")) {
        errors.password = toast.error('Wrong Password!');
    } else if(values.test(regularExpression)){
        errors.password = toast.error('password should contain 1 number and 1 special character')
    }

    return errors;
};
